import React, { useState } from 'react';
import Head from 'next/head'
import {add, getAll} from './api/hello'

export default function Home() {
  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState([]);

  const handleTask = async (e) => {
    e.preventDefault();
    await add(task)
    let tasks = await getAll() 
    .then(()=>{
      console.log(tasks)
    })
  }
  return (
    <div className="container">
      <Head>
        <title>Todo List</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <main>
      <h1>Bienvenue dans ta TODO</h1>

      <br></br>

      <form>
        <label htmlFor="task">Tache: </label>
        <input type="text" id="task" onChange={e => setTask(e.target.value)}>

        </input>


        <button onClick={handleTask}>Ajouter</button>
      </form>
        <br></br>
        <br></br>
        {tasks.map((item) => {
          return<li key={item.id}>{item.task}</li>
        })}
      </main>

    </div>
  )
}
