import axios from 'axios'

const add = (task) => {
  axios.post('http://localhost:8080/add',{
    task: task
  },{
    headers:{
      'Content-Type':'application/json'
    }
  })
}

const getAll = async () => {
  return await axios.get('http://localhost:8080/all');
};

module.exports = {add,getAll};
