import React, { useState, useEffect } from 'react';
import Head from 'next/head'
import {add, getAll} from './api/hello'

export default function Home(props) {

  const styles = {
    body : {
      backgroundColor:"#F3752B"
    },
    container : {
      marginLeft:"auto",
      marginRight:"auto",
      textAlign:"center",
      backgroundColor:"#EDEDF4",
      width:"600px",
      borderRadius:"10px solid"
    }
  }
  const [task, setTask] = useState('');
  const [tasks, setTasks] = useState([]);

  useEffect(async ()=> {
    const result = await getAll()
    .then((result)=>{
      setTasks(result.data);
    })

  }, tasks);

  const handleTask = async (e) => {
    e.preventDefault();
    await add(task)
    let result = await getAll()
    .then((result)=>{
      setTasks(result.data)
    })
  }

  return (
    <body style={styles.body}>
      <div className="container" style={styles.container} >
      <Head>
        <title>Todo List</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <main>
      <h1>Bienvenue dans ta TODO</h1>

      <br></br>

      <form>
        <label htmlFor="task">Tache: </label>
        <input type="text" id="task" onChange={e => setTask(e.target.value)}>

        </input>


        <button onClick={handleTask}>Ajouter</button>
      </form>
        <br></br>
        <br></br>
        {tasks.map((item) => {
          return<li key={item.id} id={item.id}>{item.task}</li>
        })}
      </main>

    </div>
    </body>

  )
}
